import React, {useState, useEffect } from 'react';
import queryString from 'query-string';
import io from 'socket.io-client';

import './Chat.css';

let socket;
const Chat = ({location}) => {

    const [name, setSetName] = useState('');
    const [room , setRoom] = useState('');
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const ENDPOINT = 'localhost:5000';

    //check if there are changes in ENDPOINT or location.search
    useEffect(() =>{
        const {name, room} = queryString.parse(location.search)

        socket = io(ENDPOINT);
        console.log(socket);


        setSetName(name);
        setRoom(room);

        socket.emit('login', {name: name, room: room}, ()=>
        {

        });

        return () => {
            socket.emit('disconnect');
            socket.off();
        }
    }, [ENDPOINT, location.search])

    //check if there is changes in messages
    useEffect(()=>
    socket.on('message', (message)=>{
        setMessages([...messages, message])


    }
    ), [messages]);

    //Sending message to server

    const sendMessage = (event) =>{
        event.preventDefault();
        if(message){

            socket.emit("sendMessage", message, () => setMessage('')


            );
        }

    }

    console.log(message, messages);
    return(
        <div className="ChatOuterContainer">
            <div className="container">  
                <input value={message} 
                onChange={(event)=>setMessage(event.target.value)}
                onKeyPress={(event) => {event => event.key === 'Enter' ? sendMessage(event): null}}/>


            </div>
        </div>
    )
}

export default Chat;
